import React from 'react';
import './History.css'; // เพิ่มไฟล์ CSS สำหรับสไตล์

function History({ history }) {
    const groupedMessages = history.reduce((acc, message) => {
        const date = new Date(message.timestamp).toLocaleDateString();
        if (!acc[date]) {
            acc[date] = [];
        }
        acc[date].push(message);
        return acc;
    }, {});

    return (
        <div className="history-container">
            {Object.keys(groupedMessages).map((date) => (
                <div key={date}>
                    <div className="history-date">{date === new Date().toLocaleDateString() ? 'Today' : date}</div>
                    {groupedMessages[date].map((msg, index) => (
                        <div
                            key={index}
                            className={`message-bubble ${msg.isUser ? 'user-message' : 'bot-message'}`}
                        >
                            <span>{msg.message}</span>
                            <span className="message-time">
                                {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                            </span>
                        </div>
                    ))}
                </div>
            ))}
        </div>
    );
}

export default History;
